/* eslint-disable prettier/prettier */
import { app, shell, BrowserWindow, Notification, ipcMain, session } from 'electron'
import { join } from 'path'
import { electronApp, optimizer, is } from '@electron-toolkit/utils'
import icon from '../../resources/icon.png?asset'
import { autoUpdater } from 'electron-updater'
import log from 'electron-log'

// Disable SSL certificate errors
app.commandLine.appendSwitch('ignore-certificate-errors')

function createWindow() {
  // Create the browser window.
  const mainWindow = new BrowserWindow({
    width: 1600,
    height: 980,
    show: false,
    autoHideMenuBar: true,
    ...(process.platform === 'linux' ? { icon } : {}),
    webPreferences: {
      preload: join(__dirname, '../preload/index.js'),
      sandbox: false,
      webSecurity: is.dev ? false : true // Disable web security only in development
    }
  })

  mainWindow.on('ready-to-show', () => {
    mainWindow.show()
  })

  mainWindow.webContents.setWindowOpenHandler((details) => {
    shell.openExternal(details.url)
    return { action: 'deny' }
  })

  // HMR for renderer based on electron-vite cli.
  // Load the remote URL for development or the local HTML file for production.
  if (is.dev && process.env['ELECTRON_RENDERER_URL']) {
    mainWindow.loadURL(process.env['ELECTRON_RENDERER_URL'])
  } else {
    mainWindow.loadFile(join(__dirname, '../renderer/index.html'))
  }
}

// Handle notifications from renderer
ipcMain.on('show-notification', (event, { title, body }) => {
  const notification = new Notification({
    title,
    body,
    icon: join(__dirname, '../../resources/icon.png') // Adjust icon path if needed
  })
  notification.show()
})

// This method will be called when Electron has finished
// initialization and is ready to create browser windows.
// Some APIs can only be used after this event occurs.
app.whenReady().then(() => {
  // Set app user model id for Windows
  electronApp.setAppUserModelId('taskmatrix')

  // Default open or close DevTools by F12 in development
  // and ignore CommandOrControl + R in production.
  // see https://github.com/alex8088/electron-toolkit/tree/master/packages/utils
  app.on('browser-window-created', (_, window) => {
    optimizer.watchWindowShortcuts(window)

    // Ensure DevTools can be opened in production
    window.webContents.on('before-input-event', (event, input) => {
      if (
        input.key === 'F12' ||
        (input.control && input.shift && input.key.toLowerCase() === 'i')
      ) {
        window.webContents.toggleDevTools()
      }
    })

    // Add "Inspect Element" to context menu
    window.webContents.on('context-menu', (event, params) => {
      const { Menu, MenuItem } = require('electron')
      const menu = new Menu()
      menu.append(
        new MenuItem({
          label: 'Inspect Element',
          click: () => {
            window.webContents.inspectElement(params.x, params.y)
          }
        })
      )
      menu.popup()
    })
  })

  // âœ… Fix: Disable restrictive CSP for development (prevents â€œblocked:cspâ€)
  session.defaultSession.webRequest.onHeadersReceived((details, callback) => {
    const cspValue = is.dev
      ? "default-src * 'unsafe-inline' 'unsafe-eval' data: blob:; script-src * 'unsafe-inline' 'unsafe-eval'; connect-src * 'unsafe-inline'; img-src * data: blob: 'unsafe-inline'; frame-src *; style-src * 'unsafe-inline';"
      : "default-src 'self'; script-src 'self'; connect-src 'self' https://project-station.whiteboardtec.com:5160 wss://project-station.whiteboardtec.com:5160; img-src 'self' data: blob:; style-src 'self' 'unsafe-inline'; frame-src 'none'; object-src 'none';"

    callback({
      responseHeaders: {
        ...details.responseHeaders,
        'Content-Security-Policy': [cspValue]
      }
    })
  })

  if (Notification.isSupported()) {
    console.log('Notifications are supported')
  }
  autoUpdater.checkForUpdatesAndNotify()

  // IPC test
  // ipcMain.on('ping', () => console.log('pong'))

  createWindow()

  app.on('activate', function () {
    // On macOS it's common to re-create a window in the app when the
    // dock icon is clicked and there are no other windows open.
    if (BrowserWindow.getAllWindows().length === 0) createWindow()
  })
})

// Quit when all windows are closed, except on macOS. There, it's common
// for applications and their menu bar to stay active until the user quits
// explicitly with Cmd + Q.
app.on('window-all-closed', () => {
  if (process.platform !== 'darwin') {
    app.quit()
  }
})

// Configure logging
log.transports.file.level = 'info'
autoUpdater.logger = log

// Auto-update event listeners
autoUpdater.on('checking-for-update', () => {
  log.info('Checking for update...')
})

autoUpdater.on('update-available', (info) => {
  log.info('Update available.', info)
  BrowserWindow.getAllWindows().forEach((win) => {
    win.webContents.send('update-available', info)
  })
})

autoUpdater.on('update-not-available', (info) => {
  log.info('Update not available.', info)
})

autoUpdater.on('error', (err) => {
  log.error('Error in auto-updater. ' + err)
})

autoUpdater.on('download-progress', (progressObj) => {
  let log_message = 'Download speed: ' + progressObj.bytesPerSecond
  log_message = log_message + ' - Downloaded ' + progressObj.percent + '%'
  log_message = log_message + ' (' + progressObj.transferred + '/' + progressObj.total + ')'
  log.info(log_message)
})

autoUpdater.on('update-downloaded', (info) => {
  log.info('Update downloaded', info)
  BrowserWindow.getAllWindows().forEach((win) => {
    win.webContents.send('update-downloaded', info)
  })
})

ipcMain.on('restart_app', () => {
  autoUpdater.quitAndInstall()
})

import { Provider, useDispatch } from 'react-redux'
import store from './store/store'
import Layout from './layout/DashboardLayout'
import Service from './api/Service'
import { setUserData, showStaff } from './store/userSlice'
import { useEffect } from 'react'
import { ToastContainer, toast } from 'react-toastify'
import socket, { connectSocket } from './socket'
import { loadFabricator } from './store/fabricatorSlice'
import { setRFQData } from './store/rfqSlice'
import { setProjectData } from './store/projectSlice'

const AppContent = () => {
  const dispatch = useDispatch()
  const userType = sessionStorage.getItem('userRole')

  // Electron IPC test handler
  const ipcHandle = () => {
    if (window?.electron?.ipcRenderer) {
      window.electron.ipcRenderer.send('ping')
      toast.info('ðŸ“¡ Sent IPC Ping to main process')
    } else {
      toast.warn('Electron IPC not available')
    }
  }

  // Fetch current user
  const fetchSignedinUser = async () => {
    try {
      const response = await Service.GetUserByToken()
      const userDetail = response?.data?.user
      if (!userDetail?.id) throw new Error('Invalid user')

      sessionStorage.setItem('userId', userDetail.id)
      sessionStorage.setItem('username', userDetail.username)
      dispatch(setUserData(userDetail))

      // Connect socket after user is set
      connectSocket(userDetail.id)
    } catch (err) {
      console.error('User fetch failed:', err)
      toast.error('Failed to load user')
    }
  }

  useEffect(() => {
    // Fetch user once on mount
    fetchSignedinUser()

    // Request notification permission
    if ('Notification' in window) {
      Notification.requestPermission()
    }

    const fetchAllEmployee = async () => {
      try {
        const response = await Service.FetchAllEmployee()
        const data = response?.data?.employees || []
        dispatch(showStaff(data))
      } catch (err) {
        console.error('Failed to fetch employees:', err)
        toast.error('Failed to load employees')
      }
    }

    const fetchAllFabricator = async () => {
      try {
        const response = await Service.GetAllFabricators()
        const data = response.data || []
        dispatch(loadFabricator(data))
      } catch (err) {
        console.error('Failed to fetch fabricators:', err)
        toast.error('Failed to load fabricators')
      }
    }

    const fetchInboxRFQ = async () => {
      try {
        let rfqDetail
        if (userType === 'CLIENT') {
          rfqDetail = await Service.RfqSent()
        } else {
          rfqDetail = await Service.RFQRecieved()
        }
        dispatch(setRFQData(rfqDetail.data))
      } catch (error) {
        console.error('Error fetching RFQ:', error)
      }
    }

    const fetchAllProjects = async () => {
      try {
        const response = await Service.GetAllProjects()
        const data = response.data || []
        dispatch(setProjectData(data))
      } catch (err) {
        console.error('Failed to fetch projects:', err)
        toast.error('Failed to load projects')
      }
    }

    fetchAllFabricator()
    fetchAllEmployee()
    fetchInboxRFQ()
    fetchAllProjects()

    return () => {
      if (socket.connected) {
        socket.disconnect()
        console.log('ðŸ§¹ Socket disconnected on unmount')
      }
    }
  }, [dispatch])

  return (
    <>
      <ToastContainer position="top-right" autoClose={1000} />

      {/* Main Layout */}
      <Layout />

      {/* IPC Trigger Button */}
      <div className="fixed bottom-6 right-6 flex flex-col items-center z-50">
        <button
          onClick={ipcHandle}
          className="bg-[#6bbd45] hover:bg-[#5aa33a] text-white font-semibold rounded-2xl shadow-lg px-5 py-2 transition-transform hover:scale-105"
        >
          Send IPC
        </button>
      </div>
    </>
  )
}

const App = () => (
  <Provider store={store}>
    <AppContent />
  </Provider>
)

export default App

