import { NavLink, useNavigate } from 'react-router-dom'
import LOGO from '../assets/logo.png'
import {
  ChartCandlestick,
  Home,
  MessageSquare,
  User2,
  Hourglass,
  LogOut,
  FolderOpenDot,
  X,
  Group,
  LucideComponent,
  FactoryIcon,
  FileText
} from 'lucide-react'
import { useSelector } from 'react-redux'
import Button from './fields/Button'

const Sidebar = ({ isMinimized, toggleSidebar, isMobile = false }) => {
  const userData = useSelector((state) => state?.userdata?.userDetail)

  const navigate = useNavigate()
  const userRole = sessionStorage.getItem('userRole')?.toLowerCase() || ''

  const navItems = [
    {
      label: 'Dashboard',
      to: '/dashboard',
      icon: <Home />,
      roles: [
        'admin',
        'staff',
        'department-manager',
        'deputy-manager',
        'project-manager',
        'client',
        'estimation_head',
        'system-admin',
        'user',
        'estimator',
        'sales'
      ]
    },
    {
      label: 'Estimations',
      to: 'estimation',
      icon: <Hourglass />,
      roles: ['admin', 'estimation_head', 'department-manager', 'deputy-manager', 'staff']
    },

    {
      label: 'Tasks',
      to: 'tasks',
      icon: <ChartCandlestick />,
      roles: [
        'admin',
        'staff',
        'department-manager',
        'deputy-manager',
        'project-manager',
        'estimation_head',
        'user',
        'system-admin',
        'human-resource'
      ]
    },
    {
      label: 'Chats',
      to: 'chats',
      icon: <MessageSquare />,
      roles: [
        'admin',
        'staff',
        'department-manager',
        'project-manager',
        'estimation_head',
        'deputy-manager',
        'user',
        'human-resource'
      ]
    },
    {
      label: 'Projects',
      to: 'projects',
      icon: <FolderOpenDot />,
      roles: [
        'admin',
        'staff',
        'department-manager',
        'deputy-manager',
        'project-manager',
        'client',
        'estimation_head',
        'system-admin',
        'user',
        'estimator',
        'sales'
      ]
    },
    {
      label: 'Notes',
      to: 'notes',
      icon: <FileText />,
      roles: [
        'admin',
        'staff',
        'department-manager',
        'deputy-manager',
        'project-manager',
        'client',
        'estimation_head',
        'system-admin',
        'user',
        'estimator',
        'sales'
      ]
    },
    {
      label: 'Profile',
      to: 'profile',
      icon: <User2 />,
      roles: [
        'admin',
        'user',
        'staff',
        'client',
        'connection_designer_engineer',
        'estimator',
        'estimation_head',
        'sales',
        'dept_manager',
        'project_manager',
        'system_admin',
        'human_resource'
      ]
    }
  ]

  const canView = (roles) => roles.includes(userRole.toLowerCase())

  const fetchLogout = () => {
    try {
      sessionStorage.clear()
      navigate('/')
    } catch (error) {
      console.error('Logout failed:', error)
    }
  }

  return (
    <aside
      className={`h-full transition-all duration-300 flex flex-col ${
        isMinimized ? 'w-24' : 'w-72'
      } ${isMobile ? 'shadow-2xl bg-white' : 'relative'}`}
    >
      {/* Header */}
      <div
        className={`flex items-center pt-6 pb-2 px-6 ${
          isMobile ? 'justify-between' : isMinimized ? 'justify-center' : 'justify-start'
        }`}
      >
        <div className="flex items-center w-full justify-center">
          {!isMinimized ? (
            <img
              src={LOGO}
              alt="Logo"
              className="bg-white w-56 object-contain rounded-3xl drop-shadow-sm"
            />
          ) : (
            <img
              src={LOGO}
              alt="Logo"
              className="bg-white w-16 object-contain p-1 rounded-3xl drop-shadow-sm"
