import React, { useState, useMemo } from 'react'
import {
  ComposedChart,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  Area
} from 'recharts'

const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec']

const InvoiceTrends = ({ invoices }) => {
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear())
  const [selectedMonth, setSelectedMonth] = useState(null)

  const processedChartData = useMemo(() => {
    if (selectedMonth === null) {
      // Yearly view - Monthly grouping
      return months.map((month, index) => {
        const monthlyInvoices = invoices.filter((inv) => {
          const date = new Date(inv.invoiceDate)
          return date.getFullYear() === selectedYear && date.getMonth() === index
        })

        const totalAmount = monthlyInvoices.reduce(
          (sum, inv) => sum + (inv.totalInvoiceValue || 0),
          0
        )
        return {
          name: month,
          amount: totalAmount,
          count: monthlyInvoices.length
        }
      })
    } else {
      // Monthly view - Daily grouping
      const daysInMonth = new Date(selectedYear, selectedMonth + 1, 0).getDate()
      const dailyData = []
      for (let i = 1; i <= daysInMonth; i++) {
        const dailyInvoices = invoices.filter((inv) => {
          const date = new Date(inv.invoiceDate)
          return (
            date.getFullYear() === selectedYear &&
            date.getMonth() === selectedMonth &&
            date.getDate() === i
          )
        })

        const totalAmount = dailyInvoices.reduce(
          (sum, inv) => sum + (inv.totalInvoiceValue || 0),
          0
        )
        dailyData.push({
          name: i.toString(),
          amount: totalAmount,
          count: dailyInvoices.length
        })
      }
      return dailyData
    }
  }, [invoices, selectedYear, selectedMonth])

  const { totalPeriodAmount, totalPeriodCount } = useMemo(() => {
    const amount = processedChartData.reduce((sum, item) => sum + item.amount, 0)
    const count = processedChartData.reduce((sum, item) => sum + item.count, 0)
    return { totalPeriodAmount: amount, totalPeriodCount: count }
  }, [processedChartData])

  const years = useMemo(() => {
    const currentYear = new Date().getFullYear()
    return Array.from({ length: 5 }, (_, i) => currentYear - i)
  }, [])

  return (
    <div className="bg-white p-6 rounded-2xl shadow-sm border border-gray-100">
      <div className="flex flex-col sm:flex-row sm:items-center justify-between mb-6 gap-4">
        <div>
          <h2 className="text-lg font-semibold text-gray-700 flex items-center gap-2">
            Invoice Trends
            <span className="text-sm font-normal text-gray-700">
              | Total: ${totalPeriodAmount.toLocaleString()} ({totalPeriodCount} Invoices)
            </span>
          </h2>
          <p className="text-xs text-gray-700">
            Showing trends for {selectedMonth !== null ? `${months[selectedMonth]} ` : ''}
            {selectedYear}
          </p>
        </div>

        <div className="flex items-center gap-2">
          <select
            value={selectedYear}
            onChange={(e) => setSelectedYear(parseInt(e.target.value))}
            className="p-2 text-sm border border-gray-200 rounded-lg outline-none focus:ring-2 focus:ring-green-500 bg-gray-50 font-medium"
          >
            {years.map((year) => (
              <option key={year} value={year}>
                {year}
              </option>
            ))}
          </select>
        </div>
      </div>

      {/* Month Filter Row */}
      <div className="flex items-center gap-2 mb-6 overflow-x-auto pb-2 no-scrollbar">
        <button
          onClick={() => setSelectedMonth(null)}
          className={`px-4 py-1.5 rounded-full text-xs font-semibold transition-all whitespace-nowrap ${
            selectedMonth === null
              ? 'bg-green-600 text-white shadow-md shadow-green-100'
              : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
          }`}
        >
          All Months
        </button>
        {months.map((month, index) => (
          <button
            key={month}
            onClick={() => setSelectedMonth(index)}
            className={`px-3 py-1 rounded-full text-xs font-semibold transition-all whitespace-nowrap ${
              selectedMonth === index
                ? 'bg-green-600 text-white shadow-md shadow-green-100'
                : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
            }`}
          >
            {month}
          </button>
        ))}
      </div>

      <div className="h-[300px]">
        <ResponsiveContainer width="100%" height="100%">
          <ComposedChart data={processedChartData}>
            <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#f0f0f0" />
            <XAxis
              dataKey="name"
              axisLine={false}
              tickLine={false}
              tick={{ fill: '#9ca3af', fontSize: 12 }}
              dy={10}
            />
            <YAxis
              axisLine={false}
              tickLine={false}
              tick={{ fill: '#9ca3af', fontSize: 12 }}
              tickFormatter={(value) =>
                `$${value >= 1000 ? `${(value / 1000).toFixed(1)}k` : value}`
              }
            />
            <Tooltip
              content={({ active, payload, label }) => {
                if (active && payload && payload.length) {
                  const data = payload[0].payload
                  return (
                    <div className="bg-white p-3 rounded-xl shadow-xl border border-gray-100">
                      <p className="text-xs font-bold text-gray-400 mb-1 uppercase tracking-wider">
                        {selectedMonth !== null ? `${months[selectedMonth]} ${label}` : label}
                      </p>
                      <div className="flex flex-col gap-1">
                        <p className="text-sm font-bold text-green-700">
                          Amount: ${data.amount.toLocaleString()}
                        </p>
                        <p className="text-xs font-medium text-gray-700">Invoices: {data.count}</p>
                      </div>
                    </div>
                  )
                }
                return null
              }}
            />
            <Area
              type="monotone"
              dataKey="amount"
              name="Total Amount"
              fill="#0d9488"
              fillOpacity={0.1}
              stroke="#6bbd45"
              strokeWidth={3}
            />
          </ComposedChart>
        </ResponsiveContainer>
      </div>
    </div>
  )
}

export default InvoiceTrends

import React, { useMemo } from 'react'
import { Briefcase, Info, AlertCircle } from 'lucide-react'
import { motion } from 'framer-motion'
import { motion } from 'framer-motion'

const MonthlyProjectStats = ({ tasks, projects, selectedMonth, selectedYear }) => {
  const months = [
    'January',
    'February',
    'March',
    'April',
    'May',
    'June',
    'July',
    'August',
    'September',
    'October',
    'November',
    'December'
  ]

  const workloadData = useMemo(() => {
    if (selectedMonth === null || selectedYear === null) return { projects: [], count: 0 }

    const startOfMonth = new Date(selectedYear, selectedMonth, 1)
    const endOfMonth = new Date(selectedYear, selectedMonth + 1, 0, 23, 59, 59)

    const activeProjectIds = new Set()

    tasks.forEach((task) => {
      if (!task.start_date || !task.due_date || !task.project_id) return

      const taskStart = new Date(task.start_date)
      const taskEnd = new Date(task.due_date)

      // Overlap check: task starts before month end AND task ends after month start
      if (taskStart <= endOfMonth && taskEnd >= startOfMonth) {
        activeProjectIds.add(task.project_id)
      }
    })

    const activeProjects = projects.filter((p) => activeProjectIds.has(p.id))

    return {
      projects: activeProjects,
      count: activeProjects.length
    }
  }, [tasks, projects, selectedMonth, selectedYear])

  if (selectedMonth === null || selectedYear === null) {
    return (
      <div className="bg-white p-6 rounded-2xl shadow-sm border border-gray-100 flex items-center gap-4">
        <div className="p-3 bg-blue-50 rounded-xl">
          <Info className="w-6 h-6 text-blue-500" />
        </div>
        <div>
          <h3 className="text-sm font-bold text-gray-700">Monthly Workload Insight</h3>
          <p className="text-xs text-gray-700">
            Select a specific month to see projects with active tasks during that period.
          </p>
        </div>
      </div>
    )
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="bg-white p-6 rounded-2xl shadow-sm border border-gray-100 space-y-4"
    >
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-3">
          <div className="p-2.5 bg-green-50 rounded-xl">
            <Briefcase className="w-5 h-5 text-green-600" />
          </div>
          <div>
            <h3 className="text-base font-bold text-gray-700">
              Workload for {months[selectedMonth]} {selectedYear}
            </h3>
            <p className="text-xs font-medium text-gray-400 uppercase tracking-wider">
              Projects with active tasks
            </p>
          </div>
        </div>
        <div className="flex flex-col items-end">
          <span className="text-2xl font-black text-green-600 leading-none">
            {workloadData.count}
          </span>
          <span className="text-[10px] font-bold text-gray-400 uppercase">Active Projects</span>
        </div>
      </div>

      {workloadData.count > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3">
          {workloadData.projects.map((project) => (
            <div
              key={project.id}
              className="flex items-center gap-3 p-3 bg-gray-50 rounded-xl border border-gray-100 hover:border-green-200 hover:bg-green-50/30 transition-all group"
            >
              <div className="w-8 h-8 rounded-lg bg-white border border-gray-100 flex items-center justify-center text-[10px] font-bold text-green-600 shadow-sm group-hover:scale-110 transition-transform">
                {project.projectNumber.slice(-3)}
              </div>
              <div className="flex-1 min-w-0">
                <h4 className="text-sm font-bold text-gray-700 truncate group-hover:text-green-700 transition-colors">
                  {project.name}
                </h4>
                <p className="text-[10px] font-medium text-gray-400 truncate">
                  {project.projectNumber}
                </p>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="flex items-center gap-3 p-4 bg-orange-50 rounded-xl border border-orange-100">
          <AlertCircle className="w-5 h-5 text-orange-500" />
          <p className="text-sm font-medium text-orange-700">
            No projects have tasks assigned for this month.
          </p>
        </div>
      )}
    </motion.div>
  )
}

