export default function EstimationTaskByID({ id, onClose, refresh }) {
  const [task, setTask] = useState(null)
  const [loading, setLoading] = useState(true)
  const [processing, setProcessing] = useState(false)
  const [summary, setSummary] = useState(null)
  const [showWorkSummary, setShowWorkSummary] = useState(true)
  const [refreshGroups, setRefreshGroups] = useState(0)
  const fetchTask = async () => {
    if (!id) return
    try {
      setLoading(true)
      const taskRes = await Service.GetEstimationTaskById(id)
      const taskData = taskRes.data
      setTask(taskData)

      const summaryRes = await Service.SummaryEstimationTaskById(id)
      setSummary(summaryRes.data)
    } catch (error) {
      console.error('Error fetching task:', error)
      toast.error('Failed to load estimation task')
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchTask()
  }, [id])

  const getActiveWorkID = () => {
    return task?.workinghours?.find((wh) => wh.ended_at === null)?.id || null
  }

  const formatDecimalHours = (decimalHours) => {
    const num = Number(decimalHours)
    if (isNaN(num)) return '0h 0m'
    const hours = Math.floor(num)
    const minutes = Math.round((num - hours) * 60)
    return `${hours}h ${minutes}m`
  }

  const activeWorkID = getActiveWorkID()

  const toIST = (dateString) => {
    if (!dateString) return 'â€”'
    return new Intl.DateTimeFormat('en-IN', {
      timeZone: 'Asia/Kolkata',
      day: '2-digit',
      month: 'short',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    }).format(new Date(dateString))
  }

  const handleAction = async (action) => {
    if (!task?.id) return
    try {
      setProcessing(true)
      switch (action) {
        case 'start':
          await Service.StartEstimationTaskById(task.id)
          toast.success('Task started')
          break
        case 'pause':
          if (!activeWorkID) return toast.warning('No active session to pause')
          await Service.PauseEstimationTaskById(task.id, {
            whId: activeWorkID
          })
          toast.info('Task paused')
          break
        case 'resume':
          await Service.ResumeEstimationTaskById(task.id)
          toast.success('Task resumed')
          break
        case 'end':
          if (!activeWorkID) return toast.warning('No active session to end')
          await Service.EndEstimationTaskById(task.id, { whId: activeWorkID })
          toast.success('Task completed')
          break
      }
      await fetchTask()
      if (refresh) refresh()
    } catch (error) {
      toast.error('Action failed. Please try again.')
    } finally {
      setProcessing(false)
    }
  }

  const getStatusConfig = (status) => {
    const configs = {
      ASSIGNED: {
        label: 'Assigned',
        bg: 'bg-purple-100',
        text: 'text-purple-700',
        border: 'border-purple-300'
      },
      IN_PROGRESS: {
        label: 'In Progress',
        bg: 'bg-emerald-100',
        text: 'text-emerald-700',
        border: 'border-emerald-300'
      },
      BREAK: {
        label: 'On Break',
        bg: 'bg-orange-100',
        text: 'text-orange-700',
        border: 'border-orange-300'
      },
      COMPLETED: {
        label: 'Completed',
        bg: 'bg-blue-100',
        text: 'text-blue-700',
        border: 'border-blue-300'
      }
    }
    return (
      configs[status || ''] || {
        label: status || 'Unknown',
        bg: 'bg-gray-100',
        text: 'text-gray-700',
        border: 'border-gray-300'
      }
    )
  }

  const statusConfig = getStatusConfig(task?.status)

  if (loading) {
    return (
      <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
        <div className="bg-white rounded-2xl shadow-2xl p-10 flex flex-col items-center">
          <Loader2 className="w-12 h-12 animate-spin text-green-600" />
          <p className="mt-4 text-lg font-medium text-gray-700">Loading task details...</p>
        </div>
      </div>
    )
  }

  if (!task) {
    return (
      <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
        <div className="bg-white rounded-2xl shadow-2xl p-10 text-center max-w-md">
          <div className="w-16 h-16 bg-gray-200 rounded-full mx-auto mb-4 flex items-center justify-center">
            <FileText className="w-10 h-10 text-gray-400" />
          </div>
          <p className="text-xl font-semibold text-gray-700">Task Not Found</p>
          <p className="text-gray-700 mt-2">This task may have been deleted or is inaccessible.</p>
          <button
            onClick={onClose}
            className="mt-6 px-8 py-3 bg-green-600 hover:bg-green-700 text-white font-semibold rounded-xl transition"
          >
            Close
          </button>
        </div>
      </div>
    )
  }

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 p-4">
      <div className="bg-white rounded-2xl shadow-2xl w-full h-[95vh] overflow-hidden flex flex-col">
        {/* Header */}
        <div className="sticky top-0 z-10 bg-white border-b border-gray-200 px-8 py-5 flex justify-between items-center">
          <div className="flex items-center gap-4">
            <div className="p-3 bg-green-100 rounded-xl">
              <FileText className="w-7 h-7 text-green-700" />
            </div>
            <div>
              <h2 className="text-2xl font-bold text-gray-700">Estimation Task Details</h2>
              <p className="text-sm text-gray-700">ID: #{task.id}</p>
            </div>
          </div>
          <button
            onClick={onClose}
            className="px-6 py-3 bg-gray-200 hover:bg-gray-300 text-gray-700 font-medium rounded-xl transition"
          >
            Close
          </button>
        </div>

        {/* Scrollable Content */}
        <div className="flex-1 overflow-y-auto px-6 py-6 space-y-6">
          {/* Task Info Card */}
          <div className="bg-green-50 rounded-2xl p-8 border border-green-200">
            <h3 className="text-2xl font-bold text-green-900 mb-6 flex items-center gap-3">
              <FileText className="w-7 h-7" />
              Task Information
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              <InfoItem
                icon={<Building2 />}
                label="Project"
                value={task.estimation?.projectName || 'â€”'}
              />
              <InfoItem
                icon={<Hash />}
                label="Estimation No."
                value={task.estimation?.estimationNumber || 'â€”'}
              />
              <InfoItem
                icon={<User />}
                label="Assigned To"
                value={task.assignedTo?.firstName || task.assignedTo?.username || 'â€”'}
              />
              <InfoItem icon={<Calendar />} label="Start Date" value={toIST(task.startDate)} />
              <InfoItem icon={<Calendar />} label="End Date" value={toIST(task.endDate)} />
              <div className="flex items-center gap-3">
                <div className="p-3 bg-white rounded-xl shadow-sm">
                  <div className="w-6 h-6 bg-gray-300 rounded-full"></div>
                </div>
                <div>
                  <p className="text-sm font-medium text-gray-700">Status</p>
                  <span
                    className={`inline-block mt-1 px-4 py-2 rounded-full font-semibold text-sm border-2 ${statusConfig.bg} ${statusConfig.text} ${statusConfig.border}`}
                  >
                    {statusConfig.label}
                  </span>
                </div>
              </div>
            </div>

            {/* Notes */}
            {task.notes && (
              <div className="mt-8 p-6 bg-white/70 backdrop-blur rounded-xl border border-green-100">
                <h4 className="font-semibold text-gray-700 mb-3 flex items-center gap-2">
                  <FileText className="w-5 h-5 text-green-600" />
                  Notes
                </h4>
                <div
                  className="text-gray-700 leading-relaxed prose prose-sm max-w-none"
                  dangerouslySetInnerHTML={{ __html: task.notes }}
                />
              </div>
            )}

            {/* Actions */}
            <div className="mt-8 pt-6 border-t border-green-200">
              <h4 className="text-lg font-semibold text-gray-700 mb-4">Task Controls</h4>
              <div className="flex flex-wrap items-center gap-4">
                {task.status === 'ASSIGNED' && (
                  <ActionButton
                    icon={<Play />}
                    color="emerald"
                    onClick={() => handleAction('start')}
                    disabled={processing}
                  >
                    Start Task
                  </ActionButton>
                )}
                {task.status === 'IN_PROGRESS' && (
                  <>
                    <ActionButton
                      icon={<Pause />}
                      color="amber"
                      onClick={() => handleAction('pause')}
                      disabled={processing}
                    >
                      Pause
                    </ActionButton>
                    <ActionButton
                      icon={<Square />}
                      color="red"
                      onClick={() => handleAction('end')}
                      disabled={processing}
                    >
                      End Task
                    </ActionButton>
                  </>
                )}
                {task.status === 'BREAK' && (
                  <ActionButton
                    icon={<Play />}
                    color="green"
                    onClick={() => handleAction('resume')}
                    disabled={processing}
                  >
                    Resume Task
                  </ActionButton>
                )}
                {processing && (
                  <div className="flex items-center gap-2 text-gray-700">
                    <Loader2 className="w-5 h-5 animate-spin" />
                    <span>Processing...</span>
                  </div>
                )}
