              {showWorkSummary && (
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                  <SummaryCard
                    icon={<Clock4 />}
                    label="Total Time"
                    value={formatDecimalHours(summary?.totalHours)}
