            </div>
            <div className="flex flex-col gap-6">
              <CreateLineItemGroup
                estimationId={task?.estimationId}
                onGroupCreated={() => setRefreshGroups((prev) => prev + 1)}
              />
              <LineItemGroup estimationId={task?.estimationId} refreshTrigger={refreshGroups} />
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

