import React, { useState } from 'react'

function MultipleFileUpload({ onFilesChange, initialFiles = [] }) {
  // State to hold the list of File objects
  const [files, setFiles] = useState(initialFiles)

  /**
   * Handles the file input change event.
   * Appends newly selected files to the existing file list.
   */
  const handleFileChange = (event) => {
    if (!event.target.files) {
      return // Exit if no files are selected
    }

    const selectedFiles = Array.from(event.target.files)
    const updatedFiles = [...files, ...selectedFiles]

    setFiles(updatedFiles)
    onFilesChange(updatedFiles)

    // Clear the input value to allow selecting the same file(s) again
    event.target.value = ''
  }

  /**
   * Removes a file from the list based on its index.
   */
  const removeFile = (indexToRemove) => {
    const updatedFiles = files.filter((_, index) => index !== indexToRemove)
    setFiles(updatedFiles)
    onFilesChange(updatedFiles)
  }

  /**
   * Formats file size for display.
   */
  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 B'
    const k = 1024
    const sizes = ['B', 'KB', 'MB', 'GB']
    const i = Math.floor(Math.log(bytes) / Math.log(k))
    return `${parseFloat((bytes / Math.pow(k, i)).toFixed(2))} ${sizes[i]}`
  }

  return (
    <div className="p-4 bg-white rounded-lg shadow-md border border-gray-200">
      <label className="block text-gray-700 text-sm font-medium mb-2">Upload Files</label>
      <input
        type="file"
        multiple
        onChange={handleFileChange}
        className="block w-full text-sm text-gray-700 file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
      />

      {files.length > 0 && (
        <div className="mt-4">
          <p className="text-sm font-medium text-gray-700 mb-2">Selected Files:</p>
          <ul className="space-y-2 max-h-48 overflow-y-auto pr-2">
            {files.map((file, index) => (
              <li
                key={`${file.name}-${file.lastModified}-${index}`} // More robust key
                className="flex items-center justify-between p-2 border rounded-md bg-gray-50"
              >
                <div className="flex-1 min-w-0">
                  <span
                    className="text-sm text-gray-700 font-medium block truncate"
                    title={file.name}
                  >
                    {file.name}
                  </span>
                  <span className="text-xs text-gray-700">{formatFileSize(file.size)}</span>
                </div>
                <button
                  type="button" // Prevents form submission
                  onClick={() => removeFile(index)}
                  className="ml-3 text-red-500 hover:text-red-700 focus:outline-none flex-shrink-0"
                >
                  Remove
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  )
}

export default MultipleFileUpload

'use client'

import React, { useState, useRef, useEffect, forwardRef } from 'react'
import { Search } from 'lucide-react'

const Select = ({ options = [], label, name = '', className = '', onChange, placeholder, value }) =>
  // ref: ForwardedRef<HTMLDivElement>
  {
    const [isOpen, setIsOpen] = useState(false)
    const [selectedOption, setSelectedOption] = useState(null)
    const [searchTerm, setSearchTerm] = useState('')
    const [filteredOptions, setFilteredOptions] = useState(options)
    const wrapperRef = useRef(null)
    const searchRef = useRef(null)

    // Sync filtered options when options prop changes
    useEffect(() => {
      setFilteredOptions(options)
    }, [options])

    // Sync selected option when controlled value changes
    useEffect(() => {
      if (typeof value !== 'undefined') {
        const match = options.find((o) => o.value === value) || null
        setSelectedOption(match)
      }
    }, [value, options])

    // Close dropdown when clicking outside
    useEffect(() => {
      const handleClickOutside = (event) => {
        if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
          setIsOpen(false)
        }
      }

      document.addEventListener('mousedown', handleClickOutside)
      return () => document.removeEventListener('mousedown', handleClickOutside)
    }, [])

    // Handle search input
    const handleSearch = (event) => {
      const term = event.target.value.toLowerCase()
      setSearchTerm(term)
      const filtered = options.filter(
        (option) => option && option.label.toLowerCase().includes(term)
      )
      setFilteredOptions(filtered)
    }

    // Handle option selection
    const handleSelect = (option) => {
      setSelectedOption(option)
      setSearchTerm('')
      setIsOpen(false)
      if (onChange) {
        onChange(name, String(option.value))
      }
    }

    // Highlight matching text in options
    const highlightMatch = (text, highlight) => {
      if (!highlight.trim()) {
        return text
      }
      const regex = new RegExp(`(${highlight})`, 'gi')
      const parts = text.split(regex)

      return (
        <>
          {parts.map((part, i) =>
            regex.test(part) ? (
              <mark key={i} className="bg-yellow-200 text-black">
                {part}
              </mark>
            ) : (
              part
            )
          )}
        </>
      )
    }

    return (
      <div ref={wrapperRef} className="relative w-full">
        {/* Dropdown Trigger */}
        <div
          onClick={() => {
            setIsOpen(!isOpen)
            setTimeout(() => {
              searchRef.current?.focus()
            }, 100)
          }}
          className={`flex items-center justify-between p-2 text-sm border rounded-md bg-white cursor-pointer transition-all ${
            isOpen ? 'border-blue-500 ring-2 ring-blue-100' : 'border-gray-300'
          } ${className} `}
        >
          <div className="flex-1">
            {isOpen ? (
              <div className="flex items-center">
                <Search className="w-4 h-4 text-gray-400 mr-2" />
                <input
                  ref={searchRef}
                  type="text"
                  value={searchTerm}
                  onChange={handleSearch}
                  className="w-full bg-transparent text-black outline-none"
                  placeholder="Search..."
                  onClick={(e) => e.stopPropagation()}
                />
              </div>
            ) : (
              <span className={selectedOption ? 'text-black' : 'text-gray-500'}>
                {selectedOption ? selectedOption.label : label || placeholder || 'Select an option'}
              </span>
            )}
          </div>
          <svg
            className={`w-4 h-4 text-gray-400 transition-transform ${
              isOpen ? 'transform rotate-180' : ''
            } `}
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7" />
          </svg>
        </div>

        {/* Dropdown Menu */}
        {isOpen && (
          <div className="absolute top-full z-10 w-full mt-1 text-sm bg-white border border-gray-200 rounded-md shadow-lg max-h-60 overflow-y-auto">
            {filteredOptions.length > 0 ? (
              filteredOptions.map((option) => (
                <div
                  key={option.value}
                  className="px-4 py-2 cursor-pointer hover:bg-gray-100 text-black"
                  onClick={() => handleSelect(option)}
                >
                  {highlightMatch(option.label, searchTerm)}
                </div>
              ))
            ) : (
              <div className="px-4 py-2 text-gray-500">No options found</div>
            )}
          </div>
        )}
      </div>
    )
  }

export default forwardRef(Select)

