            <div className="flex justify-end gap-6 pt-10 border-t-2 border-gray-200">
              <Button className=" flex items-center gap-3" type="submit" disabled={isSubmitting}>
                {isSubmitting ? (
                  <>Creating Project...</>
                ) : (
                  <>
                    <Sparkles className="w-6 h-6" />
                    Create Project
                  </>
                )}
              </Button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}

