export default GetMilestoneByID

import { useEffect, useState } from 'react'
import { Loader2, Plus, FileText, Calendar, User, Paperclip } from 'lucide-react'
import Service from '../../../api/Service'

import Button from '../../fields/Button'
import AddNotes from './AddNotes'
import { openFileSecurely } from '../../../utils/openFileSecurely'

const AllNotes = ({ projectId }) => {
  const [notes, setNotes] = useState([])
  const [loading, setLoading] = useState(true)
  const [showAddModal, setShowAddModal] = useState(false)

  useEffect(() => {
    if (projectId) fetchNotes()
  }, [projectId])

  const fetchNotes = async () => {
    try {
      setLoading(true)
      const response = await Service.GetProjectNotes(projectId)
      setNotes(response || [])
    } catch (error) {
      console.error('Error fetching notes:', error)
    } finally {
      setLoading(false)
    }
  }

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString('en-IN', {
      dateStyle: 'medium',
      timeStyle: 'short'
    })
  }

  return (
    <div className="space-y-4">
      <div className="flex justify-between items-center">
        <Button onClick={() => setShowAddModal(true)} className="flex items-center gap-2">
          <Plus className="w-4 h-4" /> Add Note
        </Button>
      </div>

      {loading ? (
        <div className="flex justify-center py-8">
          <Loader2 className="w-6 h-6 animate-spin text-green-600" />
        </div>
      ) : notes.length > 0 ? (
        <div className="grid gap-4">
          {notes.map((note) => (
            <div
              key={note.id}
              className="bg-white border border-gray-200 rounded-lg p-4 shadow-sm hover:shadow-md transition-shadow"
            >
              <div className="flex justify-between items-start mb-2">
                <div className="flex items-center gap-2">
                  <span
                    className={`px-2 py-1 rounded text-xs font-medium bg-gray-100 text-gray-700 border border-gray-200`}
                  >
                    {note.stage}
                  </span>
                  <span className="text-xs text-gray-700 flex items-center gap-1">
                    <Calendar className="w-3 h-3" /> {formatDate(note.createdAt)}
                  </span>
                </div>
                <div className="text-xs text-gray-700 flex items-center gap-1">
                  <User className="w-3 h-3" /> {note.createdBy?.firstName}{' '}
                  {note.createdBy?.lastName}
                </div>
              </div>

              {note.tags && (
                <div className="flex flex-wrap gap-1 mb-2">
                  {note.tags.split(',').map((tag, i) => (
                    <span
                      key={i}
                      className="px-2 py-0.5 bg-green-50 text-green-600 text-[10px] font-bold rounded-full border border-green-100 uppercase tracking-wider"
                    >
                      {tag.trim()}
                    </span>
                  ))}
                </div>
              )}

              <div
                className="text-gray-700 text-sm mb-3 prose prose-sm max-w-none"
                dangerouslySetInnerHTML={{ __html: note.content }}
              />

              {note.files && note.files.length > 0 && (
                <div className="flex flex-wrap gap-2 mt-2 pt-2 border-t border-gray-100">
                  {note.files.map((file) => (
                    <button
                      key={file.id}
                      onClick={() => openFileSecurely('project', projectId, file.id)}
                      className="flex items-center gap-1 text-xs bg-green-50 text-green-700 px-2 py-1 rounded hover:bg-green-100 transition-colors"
                    >
                      <Paperclip className="w-3 h-3" /> {file.originalName}
                    </button>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      ) : (
        <div className="text-center py-10 bg-gray-50 rounded-lg border border-dashed border-gray-300">
          <FileText className="w-8 h-8 text-gray-400 mx-auto mb-2" />
          <p className="text-gray-700">No notes found for this project.</p>
          <button
            onClick={() => setShowAddModal(true)}
            className="text-green-600 font-medium hover:underline mt-2 text-sm"
          >
            Create the first note
          </button>
        </div>
      )}

      {showAddModal && (
        <AddNotes
          projectId={projectId}
          onNoteAdded={fetchNotes}
          onClose={() => setShowAddModal(false)}
        />
      )}
    </div>
  )
}

export default AllNotes

import { useEffect, useState } from 'react'

import { Loader2, AlertCircle } from 'lucide-react'
import DataTable from '../../ui/table'
import Service from '../../../api/Service'
import GetWBSByID from './GetWBSByID'
import { Button } from '../../ui/button'
import FetchWBSTemplate from './FetchWBSTemplate'

import { useDispatch } from 'react-redux'
import { setWBSForProject } from '../../../store/wbsSlice'

const AllWBS = ({ id, stage }) => {
  const dispatch = useDispatch()
  const [wbsBundles, setWbsBundles] = useState([])
  const [loading, setLoading] = useState(false)
  const [selectedWBS, setSelectedWBS] = useState(null)
  const [showFetchTemplate, setShowFetchTemplate] = useState(false)
  const projectId = id

  // âœ… Fetch all WBS items
  const fetchAllWBS = async () => {
    console.log('fetchAllWBS called for project:', projectId, 'stage:', stage)
    try {
      setLoading(true)
      setError(null)

      const wbsBundlesResponse = await Service.GetBundleByProjectId(projectId)
      setWbsBundles(wbsBundlesResponse.data)
      console.log('Fetched WBS Bundle:', wbsBundlesResponse.data)
      dispatch(setWBSForProject({ projectId, wbs: wbsBundlesResponse.data || [] }))
    } catch (err) {
      console.error('Error fetching WBS:', err)
      setError('Failed to load WBS data')
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchAllWBS()
  }, [id, stage])

  // âœ… Define table columns for bundles
  const columns = [
    {
      accessorKey: 'bundleKey',
      header: 'Bundle Name',
      cell: ({ row }) => (
        <span className="font-medium text-gray-700">
          {row.original.name || row.original.bundle?.name || row.original.bundleKey || 'â€”'}
        </span>
      )
    },
    {
      accessorKey: 'stage',
      header: 'Stage',
      cell: ({ row }) => <span className="text-gray-700">{row.original.stage || 'â€”'}</span>
    },
    {
      accessorKey: 'totalQtyNo',
      header: 'Total Quantity',
      cell: ({ row }) => <span className="text-gray-700">{row.original.totalQtyNo || 0}</span>
    },
    {
      accessorKey: 'totalExecHr',
      header: 'Total Exec Hrs',
      cell: ({ row }) => <span className="text-gray-700">{row.original.totalExecHr || 0}</span>
    },
    {
      accessorKey: 'totalCheckHr',
      header: 'Total Check Hrs',
      cell: ({ row }) => <span className="text-gray-700">{row.original.totalCheckHr || 0}</span>
    }
  ]

  // âœ… Handle row click â€” open details
  const handleRowClick = (row) => {
    setSelectedWBS(row)
  }

  // âœ… Render loading/error states
  if (loading)
    return (
      <div className="flex justify-center items-center py-10 text-gray-700">
        <Loader2 className="w-5 h-5 animate-spin mr-2" />
        Loading WBS data...
      </div>
    )

  if (error)
    return (
      <div className="flex justify-center items-center py-10 text-red-600">
        <AlertCircle className="w-5 h-5 mr-2" /> {error}
      </div>
    )

  // âœ… Render table
  return (
    <div className="bg-white p-4 rounded-2xl shadow-sm border border-gray-100">
      <div className="flex justify-between items-center mb-4">
        <div>
          <h2 className="text-lg font-semibold text-gray-700 mb-3">
            Work Breakdown Structure (WBS)
          </h2>
          <p className="text-sm text-gray-700 mb-4">
            Total Bundles:{' '}
            <span className="font-semibold text-gray-700">{wbsBundles?.length || 0}</span>
          </p>
        </div>
        <div>
          <Button onClick={() => setShowFetchTemplate(true)}>Add New Bundle</Button>
        </div>
      </div>

      <DataTable
        columns={columns}
        data={wbsBundles || []}
        onRowClick={handleRowClick}
        detailComponent={({ row, close }) => (
          <GetWBSByID
            projectId={projectId}
            id={row.id || row.fabId || ''}
            stage={row.stage || ''}
            onClose={close}
            initialData={row}
          />
        )}
        searchPlaceholder="Search bundles by name..."
        pageSizeOptions={[10, 25, 50, 100]}
        initialSorting={[
          { id: 'totalQtyNo', desc: true },
          { id: 'bundleKey', desc: false }
        ]}
      />

      {/* âœ… Modal for WBS Details */}
      {selectedWBS && (
        <GetWBSByID
          projectId={projectId}
          id={selectedWBS.id || selectedWBS.fabId || ''}
          stage={selectedWBS.stage || ''}
          initialData={selectedWBS}
          onClose={() => setSelectedWBS(null)}
        />
      )}

      {/* âœ… Modal for Fetching WBS Templates */}
      {showFetchTemplate && (
        <div className="fixed inset-0 bg-black/20 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className="w-full max-w-4xl max-h-[90vh] overflow-y-auto">
            <FetchWBSTemplate
              id={id}
              onClose={() => setShowFetchTemplate(false)}
              onSelect={() => {
                setShowFetchTemplate(false)
                fetchAllWBS() // Refresh the list after selection
              }}
            />
          </div>
        </div>
      )}
    </div>
  )
}

export default AllWBS

