  if (error) {
    return (
      <div className="flex flex-col items-center justify-center py-12 gap-4 text-red-600">
        <AlertCircle className="w-8 h-8" />
        <p className="font-medium">{error}</p>
        <Button onClick={onClose} variant="outline">
          Close
        </Button>
