import { useEffect, useState } from 'react'
import { useForm, Controller } from 'react-hook-form'
import { useSelector } from 'react-redux'
import { toast } from 'react-toastify'
import Select from 'react-select'
import Input from '../fields/input'
import Button from '../fields/Button'
import MultipleFileUpload from '../fields/MultipleFileUpload'
import SectionTitle from '../ui/SectionTitle'
import Service from '../../api/Service'

import RichTextEditor from '../fields/RichTextEditor'

const AddSubmittal = ({ project }) => {
  const userDetail = useSelector((state) => state.userInfo.userDetail)
  const fabricators = useSelector((state) => state.fabricatorInfo.fabricatorData)
  const staff = useSelector((state) => state.userInfo.staffData)
  const [milestones, setMilestones] = useState([])
  const projectId = project?.id
  const fabricatorId = project?.fabricatorID

  const fetchMileStone = async () => {
    try {
      const response = await Service.GetProjectMilestoneById(project.id)
      if (response && response.data) {
        setMilestones(response.data)
      }
    } catch (error) {
      console.log(error)
    }
  }

  useEffect(() => {
    fetchMileStone()
  }, [project.id])

  const { register, handleSubmit, control, setValue, reset } = useForm()
  const [description, setDescription] = useState('')
  const [files, setFiles] = useState([])

  const selectedFabricator = fabricators?.find((f) => String(f.id) === String(fabricatorId))

  const pocOptions =
    selectedFabricator?.pointOfContact?.map((p) => ({
      label: `${p.firstName} ${p.middleName ?? ''} ${p.lastName}`,
      value: String(p.id)
    })) ?? []

  const recipientOptions =
    staff
      ?.filter((s) => ['ADMIN', 'SALES'].includes(s.role))
      .map((s) => ({
        label: `${s.firstName} ${s.lastName}`,
        value: String(s.id)
      })) ?? []

  const mileStoneOptions =
    milestones?.map((m) => ({
      label: m.subject || m.description || 'Unnamed Milestone',
      value: String(m.id)
    })) ?? []

  useEffect(() => {
    setValue('sender_id', String(userDetail?.id))
  }, [])

  const onSubmit = async (data) => {
    try {
      const payload = {
        ...data,
        fabricator_id: String(fabricatorId),
        project_id: String(projectId),

        description,
        files
      }
      console.log(payload)

      const formData = new FormData()
      Object.entries(payload).forEach(([key, value]) => {
        if (key === 'files' && Array.isArray(files)) {
          files.forEach((file) => formData.append('files', file))
        } else {
          formData.append(key, value)
        }
      })
      console.log(formData)

      await Service.AddSubmittal(formData)
      toast.success('Submittal Created Successfully!')

      reset()
      setDescription('')
      setFiles([])
    } catch (err) {
      console.error(err)
      toast.error('Failed to create Submittal')
    }
  }

  useEffect(() => {
    if (milestones.length > 0) {
      setValue('mileStoneId', String(milestones[0].id))
    }
  }, [milestones])

  return (
    <div className="w-full mx-auto bg-white p-4 rounded-xl shadow">
      <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
        <SectionTitle title="Fabrication & Routing" />

        {/* Sender (Fabricator POC) */}
        <Controller
          name="sender_id"
          control={control}
          render={({ field }) => (
            <Select
              placeholder="Fabricator Contact"
              options={pocOptions}
              value={pocOptions.find((o) => o.value === field.value) ?? null}
              onChange={(option) => field.onChange(option?.value || '')}
            />
          )}
        />

        {/* Recipient (WBT Team) */}
        <Controller
          name="recepient_id"
          control={control}
          rules={{ required: 'Recipient required' }}
          render={({ field }) => (
            <Select
              placeholder="WBT Recipient *"
              options={recipientOptions}
              value={recipientOptions.find((o) => o.value === field.value) ?? null}
              onChange={(option) => field.onChange(option?.value || '')}
            />
          )}
        />

        <Controller
          name="mileStoneId"
          control={control}
          render={({ field }) => (
            <Select
              placeholder="Select Project Milestone"
              options={mileStoneOptions}
              value={mileStoneOptions.find((o) => o.value === field.value) ?? null}
              onChange={(option) => field.onChange(option?.value || null)}
            />
          )}
        />

        <SectionTitle title="Details" />

        <Input
          label="Subject"
          placeholder="Enter Submittal Subject"
          {...register('subject', { required: true })}
        />

        <div className="space-y-1">
          <label className="text-sm font-medium text-gray-700">Description</label>
          <RichTextEditor
            value={description}
            onChange={setDescription}
            placeholder="Enter submittal description..."
          />
        </div>

        <SectionTitle title="Files" />

        <MultipleFileUpload onFilesChange={setFiles} />

        <div className="flex justify-end">
          <Button type="submit">Submit Submittal</Button>
        </div>
      </form>
    </div>
  )
}

export default AddSubmittal

import { useEffect, useState } from 'react'
import DataTable from '../ui/table'

import { Loader2, Inbox } from 'lucide-react'
import Service from '../../api/Service'
import GetSubmittalByID from './GetSubmittalByID'

const AllSubmittals = ({ submittalData }) => {
  const [submittals, setSubmittals] = useState([])
  const [loading, setLoading] = useState(true)
  console.log(submittalData)

  const userRole = sessionStorage.getItem('userRole')

  const fetchSubmittals = async () => {
    try {
      setLoading(true)
      let result

      if (userRole === 'CLIENT') result = await Service.SubmittalSent()
      else result = await Service.SubmittalRecieved()

      const data = Array.isArray(result?.data) ? result.data : []

      const normalized = data.map((item) => ({
        ...item,
        milestone: item.mileStoneBelongsTo || item.milestone || null,
        recipient: item.recepients || null,
        sender: item.sender || null,
        createdAt: item.createdAt || item.date || null,
        statusLabel:
          item.isAproovedByAdmin === true
            ? 'APPROVED'
            : item.isAproovedByAdmin === false
              ? 'REJECTED'
              : 'PENDING'
      }))

      setSubmittals(normalized)
    } catch {
      setSubmittals([])
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    if (submittalData && submittalData.length > 0) {
      setSubmittals(submittalData)
      setLoading(false)
    } else {
      fetchSubmittals()
    }
  }, [])

  const columns = [
    { accessorKey: 'subject', header: 'Subject' },

    {
      accessorKey: 'sender',
      header: 'Sender',
      cell: ({ row }) => {
        const s = row.original.sender
        return s ? `${s.firstName ?? ''} ${s.lastName ?? ''}`.trim() : 'â€”'
      }
    },

    {
      accessorKey: 'status',
      header: 'Status',
      cell: ({ row }) => (
        <span
          className={`px-2 py-1 rounded-full text-xs font-medium ${
            row.original.status === true
              ? 'bg-yellow-100 text-yellow-700'
              : 'bg-green-100 text-green-700'
          }`}
        >
          {row.original.status === true ? 'Pending' : 'Responded'}
        </span>
      )
    },

    {
      accessorKey: 'createdAt',
      header: 'Created',
      cell: ({ row }) => new Date(row.original.date).toLocaleString()
    }
  ]

  if (loading) {
    return (
      <div className="flex flex-col items-center gap-2 py-12 text-gray-700">
        <Loader2 className="animate-spin w-6 h-6" /> Loading Submittals...
      </div>
    )
  }

  if (!submittals.length) {
    return (
      <div className="flex flex-col items-center gap-2 py-16 text-gray-700">
        <Inbox className="w-10 h-10 text-gray-400" />
        <p>No Submittals Found</p>
      </div>
    )
  }

  return (
    <div className="bg-white p-2 rounded-xl shadow-md">
      <DataTable
        columns={columns}
        data={submittals}
        detailComponent={({ row }) => <GetSubmittalByID id={row.id} />}
        searchPlaceholder="Search Submittals..."
        pageSizeOptions={[5, 10, 25]}
      />
    </div>
  )
}

