import { useState, useEffect, useRef, useCallback } from 'react'

export function useCachedApi({
  cacheKey,
  fetchFn,
  expiry = 600000, // default: 10 minutes
  debounceMs = 0,
  fetchArgs = []
}) {
  const [data, setData] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  // Use globalThis.setTimeout for browser compatibility (avoid NodeJS type issue)
  const debounceRef = useRef(null)
  const argsKey = JSON.stringify(fetchArgs)

  // Helper: read from cache
  const getCache = () => {
    try {
      const item = localStorage.getItem(cacheKey)
      if (!item) return null
      const { ts, args, value } = JSON.parse(item)
      // Invalidate if fetchArgs have changed (e.g., search param)
      if (JSON.stringify(args) !== argsKey) return null
      if (expiry > 0 && Date.now() - ts > expiry) return null
      return value
    } catch {
      return null
    }
  }

  // Helper: write to cache
  const setCache = (val) => {
    localStorage.setItem(cacheKey, JSON.stringify({ ts: Date.now(), args: fetchArgs, value: val }))
  }

  // Helper: invalidate cache
  const invalidate = useCallback(() => {
    localStorage.removeItem(cacheKey)
    setData(null)
    setLoading(true)
  }, [cacheKey])

  const fetchData = useCallback(async () => {
    setLoading(true)
    setError(null)
    try {
      const value = await fetchFn(...fetchArgs)
      setData(value)
      setCache(value)
      setLoading(false)
    } catch (e) {
      setError(e?.message || 'Failed to fetch')
      setLoading(false)
    }
  }, [fetchFn, argsKey])

  // Initial+on fetchArgs change: try cache, fetch if none
  useEffect(() => {
    // Debounce logic for search/filter
    if (debounceRef.current) clearTimeout(debounceRef.current)
    setLoading(true)
    const cached = getCache()
    if (cached) {
      setData(cached)
      setLoading(false)
    } else {
      if (debounceMs > 0) {
        debounceRef.current = setTimeout(() => {
          fetchData()
        }, debounceMs)
      } else {
        fetchData()
      }
    }
    // eslint-disable-next-line
  }, [argsKey, cacheKey])

  /** Manual refresh (ignores cache and refetches) */
  const refresh = useCallback(() => {
    invalidate()
    fetchData()
  }, [invalidate, fetchData])

  return { data, loading, error, refresh, invalidate }
}

import { useEffect, useState } from 'react'
import { AddEstimation, AllEstimation } from '../components'
import Service from '../api/Service'
import AllEstimationTask from '../components/estimation/estimationTask/AllEstimationTask'
import AllAssignedTask from '../components/estimation/estimationTask/AllAssignedTask'

const EstimationLayout = () => {
  const [activeTab, setActiveTab] = useState('allEstimation')
  const [estimation, setEstimation] = useState([])
  const userRole = sessionStorage.getItem('userRole')?.toUpperCase()
  const fetchAllEstimation = async () => {
    try {
      let response
      if (userRole === 'ESTIMATION_HEAD' || userRole === 'ADMIN') {
        response = await Service.AllEstimation()
      } else {
        response = await Service.GetEstimationTaskForAssignee()
      }
      console.log(response?.data)
      setEstimation(response?.data)
    } catch (error) {
      console.error('Error fetching estimations:', error)
    }
  }

  useEffect(() => {
    fetchAllEstimation()
  }, [])

  return (
    <div className="w-full overflow-y-hidden overflow-x-hidden">
      <div className="flex flex-col w-full h-full">
        <div className="px-3 flex flex-col justify-between items-start backdrop-blur-2xl bg-[#6bbd45] border-b rounded-t-2xl">
          <h1 className="text-2xl py-2 font-bold text-white">Estimation Detail</h1>
          <div className="flex flex-row w-full">
            <button
              onClick={() => setActiveTab('allEstimation')}
              className={`px-1.5 md:px-4 py-2 rounded-lg rounded-b ${
                activeTab === 'allEstimation'
                  ? 'text-base md:text-base bg-white/70 backdrop-xl text-gray-800 font-bold'
                  : 'md:text-base text-sm text-white font-semibold'
              }`}
            >
              All Estimations
            </button>

            <button
              onClick={() => setActiveTab('allAssignedTask')}
              className={`px-1.5 md:px-4 py-2 rounded-lg rounded-b ${
                activeTab === 'allAssignedTask'
                  ? 'text-base md:text-base bg-white/70 backdrop-xl text-gray-800 font-bold'
                  : 'md:text-base text-sm text-white font-semibold'
              }`}
            >
              {userRole === 'ESTIMATION_HEAD' || userRole === 'ADMIN'
                ? 'Add Estimation'
                : 'All Assigned Task'}
            </button>
          </div>
        </div>
      </div>

      <div className="flex-1 min-h-0 bg-white p-2 rounded-b-2xl overflow-y-auto">
        {activeTab === 'allEstimation' &&
          (userRole === 'ESTIMATION_HEAD' || userRole === 'ADMIN' ? (
            <AllEstimation estimations={estimation} />
          ) : (
            <AllEstimationTask estimations={estimation} />
          ))}
        {activeTab === 'allAssignedTask' &&
          (userRole === 'ESTIMATION_HEAD' || userRole === 'ADMIN' ? (
            <AddEstimation />
          ) : (
            <AllAssignedTask />
          ))}
      </div>
    </div>
  )
}

export default EstimationLayout

import React, { useState } from 'react'
